  const [jpgImage, setJpgImage] = useState(null);

  function handleFileChange(event) {
    setPngFile(event.target.files[0]);
  }

function convertToJPG() {
  const pngImage = new Image();
  const canvas = document.createElement('canvas');
  const ctx = canvas.getContext('2d');
  let imgData;

  pngImage.onload = function() {
    canvas.width = pngImage.width;
    canvas.height = pngImage.height;
    ctx.drawImage(pngImage, 0, 0);
    imgData = canvas.toDataURL('image/jpeg');
    setJpgImage(imgData);
  };
  pngImage.src = URL.createObjectURL(pngFile);
}

function downloadJPG() {
  const link = document.createElement('a');
  link.download = 'image.jpg';
  link.href = jpgImage;
  link.click();
}
// png to webp
const [webp, setWebp] = useState(null);
  const fileInput = useRef(null);

  const handleFileChange1 = async (e) => {
    const file = e.target.files[0];
    if (!file) return;
    if (file.type !== 'image/png') {
      alert('Please select a PNG image.');
      return;
    }
    const imageUrl = URL.createObjectURL(file);
    const image = new Image();
    image.src = imageUrl;
    await new Promise((resolve) => {
      image.onload = resolve;
    });
    const canvas = document.createElement('canvas');
    canvas.width = image.width;
    canvas.height = image.height;
    const ctx = canvas.getContext('2d');
    ctx.drawImage(image, 0, 0);
    const webpData = canvas.toDataURL('image/webp');
    setWebp(webpData);
  };
  const handleDownload1 = () => {
    if (!webp) return;
    const link = document.createElement('a');
    link.download = 'converted.webp';
    link.href = webp;
    link.click();
  };

  // doc to pdf
  const [pdf, setPdf] = useState(null);
  const fileInput2 = useRef(null);

  const handleFileChange2 = async (e) => {
    const file = e.target.files[0];
    if (!file) return;
    if (file.type !== 'application/vnd.openxmlformats-officedocument.wordprocessingml.document') {
      alert('Please select a DOCX file.');
      return;
    }
    const pdfData = await
    // Perform the conversion to PDF here
    // For example, you can use a library or an API to convert the file
    // and set the result to the pdf state variable
    setPdf(pdfData);
  };

  const handleDownload2 = () => {
    if (!pdf) return;
    fileDownload(pdf, 'converted.pdf');
  };
  return (
